CREATE DEFINER=`reboquerecife03`@`%` FUNCTION `rewards`(championship_id int) RETURNS int(11)
    DETERMINISTIC
BEGIN

	 
	declare user_bet,match_bet varchar(50);
 	declare done int default false;
	declare success_bet int default 0;
	
	declare cur cursor for
	select 
		gbm_championship_match.user_bet, 
		if(gbm_team_home_goals > gbm_team_challenger_goals, 'HOME',if(gbm_team_home_goals = gbm_team_challenger_goals,'DRAW','CHALLENGER')) match_bet 
	from gbm_championship_match 
	where gbm_championship_id = championship_id;

	declare continue handler for not found set done = true;
	
	open cur;

	read_loop: loop
		 fetch cur into user_bet,match_bet; 
		if done then
			leave read_loop;
		end if;
		if user_bet = match_bet then
			set success_bet = success_bet +1;
		end if;
	end loop;
	close cur;

RETURN success_bet;
END ;;
