CREATE DEFINER=`reboquerecife03`@`%` PROCEDURE `p_insert_championship`(user_id int)
BEGIN
	declare team_home_id, team_challenger_id, championship_id int;
	declare user_name varchar(450);
 	declare done int default false;
	
	declare cursorHome cursor for
	select  
		id
		from gbm_team where id % 2 = 0 order by rand();	

	declare cursorChallenger cursor for
	select  
		id
		from gbm_team where id % 2 = 1 order by rand();

	 

	declare continue handler for not found set done = true;

	open cursorHome;
	open cursorChallenger;
	select username into user_name from gbm_user where id = user_id;
	insert into gbm_championship (name,gbm_user_id) values(concat(user_name,'\'s Championship'),user_id);
	select last_insert_id() into championship_id;
	read_loop: loop
		 fetch cursorHome into team_home_id;
		 fetch cursorChallenger into team_challenger_id;
		if done then
			leave read_loop;
		end if;
		insert into gbm_championship_match(gbm_championship_id,gbm_team_home_id,gbm_team_challenger_id)values(championship_id,team_home_id,team_challenger_id);
	end loop;
	close cursorHome;
	close cursorChallenger;
END ;;
